$.fn.modalLoading = function(is = 'show', isOnClickRemove = true) {if(!$(".modal-backdrop-loading.lodrl")[0]){this.append(`<div class="modal-backdrop-loading lodrl d-flex fade" style="display: none;"><div class="bck"></div><span class="m-auto"><div class="loader-4567"><svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 66 66" height="150px" width="150px" class="spinner-ml"><circle stroke="url(#gradient-loader)" r="20" cy="33" cx="33" stroke-width="1" fill="transparent" class="path-ml"></circle><linearGradient id="gradient-loader"> <stop stop-opacity="1" stop-color="#8f00f4" offset="0%"></stop><stop stop-opacity="0" stop-color="#af3dff" offset="100%"></stop></linearGradient></svg></div></span></div>`
);}if(is == 'show'){$(".modal-backdrop-loading.lodrl").addClass('show');$('.modal-backdrop-loading.lodrl').on('click', function () {if(isOnClickRemove == true){$(".modal-backdrop-loading.lodrl").removeClass('show');setTimeout(()=>{$(".modal-backdrop-loading.lodrl").remove()}, 1000);}});}else if(is == 'hide'){$(".modal-backdrop-loading.lodrl").removeClass('show');setTimeout(()=>{$(".modal-backdrop-loading.lodrl").remove()}, 1000);}};