$.fn.modalLoading = function(is = 'show', isOnClickRemove = true) {if(!$(".modal-backdrop-loading.lodrl")[0]){this.append(`<div class="modal-backdrop-loading lodrl d-flex fade" style="display: none;"><span class="m-auto"><i class="gg-spinner text-white float-inline-start"></i></span></div>`);}if(is == 'show'){$(".modal-backdrop-loading.lodrl").addClass('show');$('.modal-backdrop-loading.lodrl').on('click', function () {if(isOnClickRemove == true){$(".modal-backdrop-loading.lodrl").removeClass('show');setTimeout(()=>{$(".modal-backdrop-loading.lodrl").remove()}, 1000);}});}else if(is == 'hide'){$(".modal-backdrop-loading.lodrl").removeClass('show');setTimeout(()=>{$(".modal-backdrop-loading.lodrl").remove()}, 1000);}};